mapper.select:
SELECT
FROM paper LEFT JOIN sys_teacher_student sts ON sts.student_id = paper.student_id LEFT JOIN sys_user su ON su.user_id = sts.teacher_id LEFT JOIN sys_user su2 ON su2.user_id = sts.student_id
AND paper.student_id = #{studentId} AND sts.link_status = 1 AND del_flag = 0
AND paper.name like concat('%',#{name},'%')
AND paper.status = #{status}
AND paper.create_time LIKE concat('%',#{createTime},'%')
order by ${sort} ${order}
order by paper.id desc
limit #{offset}, #{limit}
