mapper.select:
SELECT sts.student_id, sts.paper_title FROM sys_teacher_student sts WHERE teacher_id = #{teacherId} AND link_status = 1
order by ${sort} ${order}
order by sts.student_id desc
limit #{offset}, #{limit}

mapper.update:
UPDATE sys_teacher_student
SET paper_title = #{paperTitle}
where teacher_id = #{teacherId}
and student_id = #{studentId}
and link_status = 1

UPDATE sys_teacher_student
SET link_status = #{linkStatus}
WHERE
	student_id = #{studentId}
AND teacher_id = #{teacherId}
AND link_status = 1
