mapper.select:
SELECT
    sf.*, su.`name` studentName
FROM
    sys_file sf
LEFT JOIN sys_user su ON su.user_id = sf.user_id

    AND sf.user_id IN (
    SELECT
    student_id
    FROM
    sys_teacher_student
    WHERE
    teacher_id = #{teacherId}
    )

        and sf.user_id = #{userId}


        and sf.name LIKE concat('%',#{name},'%')


        and sf.create_date LIKE concat('%',#{createDate},'%')


        and sf.read_status = #{readStatus}




        order by ${sort} ${order}


        order by sf.user_id desc



    limit ${offset}, ${limit}

SELECT count(1) FROM sys_file sf LEFT JOIN sys_user su ON su.user_id = sf.user_id
AND sf.user_id IN ( SELECT student_id FROM sys_teacher_student WHERE teacher_id = #{teacherId} )
and sf.user_id = #{userId}
and sf.name LIKE concat('%',#{name},'%')
and sf.create_date LIKE concat('%',#{createDate},'%')
and sf.read_status = #{readStatus}
